mapper.delete:

	delete from agent
	where 
	appid =#{appid}
	 and 
	 guid = #{guid}

mapper.update:
update agent
state=#{state}, openid=#{openid}, status=#{status}, userNum1=userNum1+1, userNum2=userNum2+1, userNum3=userNum3+1, income1=income1+#{income1}, income2=income2+#{income2}, income3=income3+#{income3}, balance=balance+#{balance} freeze=#{freeze} syn=#{syn}
where guid=#{guid} and appid=#{appid}

mapper.select:

select * from agent 
	where 
 appid=#{appid} 
 	and
  guid = #{guid};


  select * from agent
    
			and appid = #{appid} 
			and guid = #{guid} 
			and name like '%${name}%'
			
				and DATE(applyTime) >= #{applyTime}
			
			
				and DATE(checkTime) <= #{checkTime}
			
		
			ORDER BY applyTime DESC
			LIMIT ${startIndex},${pageSize}


  SELECT count(*) FROM  agent
    
			and appid = #{appid} 
			and guid = #{guid} 
			and name like '%${name}%'
			
				and DATE(applyTime) >= #{applyTime}
			
			
				and DATE(checkTime) <= #{checkTime}
			
		
			ORDER BY applyTime DESC
			LIMIT ${startIndex},${pageSize}
