count(*)
FROM
income_log

	
			 and orderid=#{orderid}
	
	
			 and appid=#{appid} 
	
	
			 and userLevel=#{userLevel} 
	
	
			 and superGuid=#{superGuid} 
	
	
			 and guid=#{guid}

SELECT
*
FROM
income_log

	
		and orderid=#{orderid} 
	
	
		and appid=#{appid}
	
	
			 and userLevel=#{userLevel} 
	
	
		 and superGuid=#{superGuid} 
	
	
		and guid=#{guid} 
	
	
order by createTime desc
	LIMIT #{startIndex}, #{pageSize}

